select date_format(SALES_DATE,"%Y-%m-%d") as "SALES_DATE", PRODUCT_ID, USER_ID, SALES_AMOUNT from ONLINE_SALE where date_format(SALES_DATE, "%m") = 3 union select date_format(SALES_DATE,"%Y-%m-%d") as "SALES_DATE", PRODUCT_ID, null as USER_ID, SALES_AMOUNT from OFFLINE_SALE where date_format(SALES_DATE, "%m") = 3 order by SALES_DATE, PRODUCT_ID, USER_ID;